import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import {Card} from 'react-bootstrap';
import {CardDeck} from 'react-bootstrap';
import '../index.css'; 

class Counter extends Component {
    state = {
        count: 0
    };
    PRODUCTS = [
        {id:1,handle: 'Sporting Goods', price: '$49.99', stocked: false, title: 'Football',body:""},
      ];
    render() { 

        return (
            <div>
            <div>{this.header()}</div>
            <CardDeck className="m-2 group">
            <ul className="list-group list-group-horizontal align-items-stretch flex-wrap">
                {this.renderTags()}
            </ul>
            </CardDeck>
            </div>
        );
    }

    header(){

        return (
                <div className="header">
            <span className={this.getBadgeClasses()}>{this.formatCounter()}</span>
                    </div>
        );
    }



    handelerIncrement = product => {
        this.setState({ stocked : product.stocked= !product.stocked })
        this.setState({ count : product.stocked==true ? this.state.count+=1 : this.state.count-=1 })
    }

    renderTags(){
        return this.PRODUCTS.map((currElement) => 
            <li className="list-group-item border-0">
         <Card key={currElement.id} className="col-sm m-2" style={{ width: '18rem' }}>
            <Card.Img variant="top" src="https://developer.softbankrobotics.com/sites/default/files/2019-10/pepper_selector_2NEW.png" />
            <Card.Body>
                <Card.Title>{currElement.title}</Card.Title>
                <Card.Text>
                {currElement.title}
                </Card.Text>
                <Button onClick={() => this.handelerIncrement(currElement)} variant="primary">{currElement.stocked == false ? "Add To Card"  : "Remove From Card" }</Button>
            </Card.Body>
            </Card> 
            </li>
          )
    }
    getBadgeClasses() {
        let classes = "badge m-3 badge-";
        classes += this.state.count == 0 ? "warning" : "primary";
        return classes;
    }

    formatCounter(){
        const {count} = this.state.count;
        return count == 0 ? "Zero" : this.state.count;
    }
}

 
export default Counter;
