import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import {Card} from 'react-bootstrap';
import {CardGroup} from 'react-bootstrap';


class Counter extends Component {
    state = {
        count: 0 ,
        tags:['tag1','tag2','tag3']
    };


    render() { 

        return (
            <div>
            <span className={this.getBadgeClasses()}>{this.formatCounter()}</span>
            <button onClick={this.handelerIncrement} className="btn btn-secondary btn-sm">Increacment</button>
            <div className="container">
            <div className="row">
                {this.renderTags()}
            </div>
            </div>           
            </div>
        );
    }

    handelerIncrement = () =>{
    this.setState({ count: this.state.count + 1 });
    }
    renderTags(){
        if (this.state.tags.length == 0)return <p>There are no tags</p>;
        return <ul>{this.state.tags.map( tag => 
        /* <Card key={tag} className="col-sm" style={{ width: '18rem' }}>
            <Card.Img variant="top" src="https://developer.softbankrobotics.com/sites/default/files/2019-10/pepper_selector_2NEW.png" />
            <Card.Body>
                <Card.Title>{tag}</Card.Title>
                <Card.Text>
                {tag}
                </Card.Text>
                <Button onClick={this.handelerIncrement} variant="primary">Add To Card</Button>
            </Card.Body>
            </Card> */


        <CardGroup>
            <Card>
                <Card.Img variant="top" src="holder.js/100px160" />
                <Card.Body>
                <Card.Title>Card title</Card.Title>
                <Card.Text>
                    This is a wider card with supporting text below as a natural lead-in to
                    additional content. This content is a little bit longer.
                </Card.Text>
                </Card.Body>
                <Card.Footer>
                <Button onClick={this.handelerIncrement} className="text-muted">Last updated 3 mins ago</Button>
                </Card.Footer>
            </Card>

        </CardGroup>
        )}
        </ul>
    }
    getBadgeClasses() {
        let classes = "badge m-3 badge-";
        classes += this.state.count == 0 ? "warning" : "primary";
        return classes;
    }

    formatCounter(){
        const {count} = this.state.count;
        return count == 0 ? "Zero" : this.state.count;
    }
}

 
export default Counter;
