import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import {Card} from 'react-bootstrap';
import {CardDeck} from 'react-bootstrap';
import '../index.css'; 

class Counter extends Component {
    state = {
        count: 0
    };
    PRODUCTS = [
        {id:1,handle: 'ocean-blue-shirt', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:2,handle: 'classic-varsity-top', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:3,handle: 'classic-varsity-top', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:4,handle: 'classic-varsity-top', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:5,handle: 'yellow-wool-jumper', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:6,handle: 'floral-white-top', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:7,handle: 'striped-silk-blouse', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:8,handle: 'classic-leather-jacket', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:9,handle: 'dark-denim-top', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:10,handle: 'navy-sport-jacket', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:11,handle: 'dark-winter-jacket', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:12,handle: 'black-leather-bag', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:13,handle: 'zipped-jackett', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:14,handle: 'ocean-blue-shirt', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:15,handle: 'ocean-blue-shirt', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:16,handle: 'ocean-blue-shirt', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:17,handle: 'ocean-blue-shirt', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:18,handle: 'ocean-blue-shirt', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:19,handle: 'ocean-blue-shirt', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:20,handle: 'ocean-blue-shirt', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:21,handle: 'ocean-blue-shirt', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:22,handle: 'ocean-blue-shirt', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
        {id:23,handle: 'ocean-blue-shirt', stocked: false, title: 'Football',tags:"men",src:'https://burst.shopifycdn.com/photos/young-man-in-bright-fashion_925x.jpg'},
      ];
    render() { 

        return (
            <div>
            <div>{this.header()}</div>
            <CardDeck className="m-2 group">
            <ul className="list-group list-group-horizontal align-items-stretch flex-wrap">
                {this.renderTags()}
            </ul>
            </CardDeck>
            </div>
        );
    }

    header(){

        return (
                <div className="header">
            <span className={this.getBadgeClasses()}>{this.formatCounter()}</span>
                    </div>
        );
    }



    handelerIncrement = product => {
        this.setState({ stocked : product.stocked= !product.stocked })
        this.setState({ count : product.stocked==true ? this.state.count+=1 : this.state.count-=1 })
    }

    renderTags(){
        return this.PRODUCTS.map((currElement) => 
            <li className="list-group-item border-0">
         <Card key={currElement.id} className="col-sm m-2" style={{ width: '18rem' }}>
            <Card.Img variant="top" src={currElement.src} />
            <Card.Body>
                <Card.Title>{currElement.title}</Card.Title>
                <Card.Text>
                {currElement.title}
                </Card.Text>
                <Button onClick={() => this.handelerIncrement(currElement)} variant="primary">{currElement.stocked == false ? "Add To Card"  : "Remove From Card" }</Button>
            </Card.Body>
            </Card> 
            </li>
          )
    }
    getBadgeClasses() {
        let classes = "badge m-3 badge-";
        classes += this.state.count == 0 ? "warning" : "primary";
        return classes;
    }

    formatCounter(){
        const {count} = this.state.count;
        return count == 0 ? "Zero" : this.state.count;
    }
}

 
export default Counter;
