import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import {Card} from 'react-bootstrap';
import {CardDeck} from 'react-bootstrap';
import '../index.css'; 

class Counter extends Component {
    state = {
        count: 0 ,
        tags:['tag1','tag2','tag3',"tsh5",]
    };


    render() { 

        return (
            <div>
            <div>{this.header()}</div>
            <CardDeck className="m-2 group">
            <ul>
                <li ClassName="list-group-item border-0">
                {this.renderTags()}
                </li>
            </ul>
            </CardDeck>
            </div>
        );
    }

    handelerIncrement = () =>{
    this.setState({ count: this.state.count + 1 });
    }
    renderTags(){
        if (this.state.tags.length == 0)return <p>There are no tags</p>;
        return this.state.tags.map( tag => 
         <Card key={tag} className="col-sm m-2" style={{ width: '18rem' }}>
            <Card.Img variant="top" src="https://developer.softbankrobotics.com/sites/default/files/2019-10/pepper_selector_2NEW.png" />
            <Card.Body>
                <Card.Title>{tag}</Card.Title>
                <Card.Text>
                {tag}
                </Card.Text>
                <Button onClick={this.handelerIncrement} variant="primary">Add To Card</Button>
            </Card.Body>
            </Card> 

        )
    }
    getBadgeClasses() {
        let classes = "badge m-3 badge-";
        classes += this.state.count == 0 ? "warning" : "primary";
        return classes;
    }

    formatCounter(){
        const {count} = this.state.count;
        return count == 0 ? "Zero" : this.state.count;
    }
    header(){

        return (
                <div className="Header">
            <span className={this.getBadgeClasses()}>{this.formatCounter()}</span>
            <button onClick={this.handelerIncrement} className="btn btn-secondary btn-sm">Increacment</button>
                    </div>
        );
    }
}

 
export default Counter;
