import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import {Card} from 'react-bootstrap';
import {CardDeck} from 'react-bootstrap';
import '../index.css'; 

class Counter extends Component {
    state = {
        count: 0
    };
    PRODUCTS = [
        {category: 'Sporting Goods', price: '$49.99', stocked: false, name: 'Football'},
        {category: 'Sporting Goods', price: '$9.99', stocked: false, name: 'Baseball'},
        {category: 'Sporting Goods', price: '$29.99', stocked: false, name: 'Basketball'},
        {category: 'Electronics', price: '$99.99', stocked: false, name: 'iPod Touch'},
        {category: 'Electronics', price: '$399.99', stocked: false, name: 'iPhone 5'},
        {category: 'Electronics', price: '$199.99', stocked: false, name: 'Nexus 7'}
      ];
    render() { 

        return (
            <div>
            <div>{this.header()}</div>
            <CardDeck className="m-2 group">
            <ul className="list-group list-group-horizontal align-items-stretch flex-wrap">
                {this.renderTags()}
            </ul>
            </CardDeck>
            </div>
        );
    }

    header(){

        return (
                <div className="header">
            <span className={this.getBadgeClasses()}>{this.formatCounter()}</span>
                    </div>
        );
    }




    handelerIncrement = (index) =>{
    console.log(index)
    this.setState({count:this.state.count +1});
    }
    renderTags(){
        return this.PRODUCTS.map((currElement, index) => 
            <li className="list-group-item border-0">
         <Card key={currElement.name} className="col-sm m-2" style={{ width: '18rem' }}>
            <Card.Img variant="top" src="https://developer.softbankrobotics.com/sites/default/files/2019-10/pepper_selector_2NEW.png" />
            <Card.Body>
                <Card.Title>{currElement.name}</Card.Title>
                <Card.Text>
                {currElement.name}
                </Card.Text>
                <Button onClick={this.handelerIncrement(index)} variant="primary">Add To Card</Button>
            </Card.Body>
            </Card> 
            </li>
          )
    }
    getBadgeClasses() {
        let classes = "badge m-3 badge-";
        classes += this.state.count == 0 ? "warning" : "primary";
        return classes;
    }

    formatCounter(){
        const {count} = this.state.count;
        return count == 0 ? "Zero" : this.state.count;
    }
}

 
export default Counter;
