import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import {Card} from 'react-bootstrap';
import {CardDeck} from 'react-bootstrap';
import '../index.css'; 

class ProductTable extends React.Component {
    render() {
      const filterText = this.props.filterText;
      const inStockOnly = this.props.inStockOnly;
  
      const rows = [];
      let lastCategory = null;
  
      this.props.products.forEach((product) => {
        if (product.name.indexOf(filterText) === -1) {
          return;
        }
        if (inStockOnly && !product.stocked) {
          return;
        }
        if (product.category !== lastCategory) {
          rows.push(
            <ProductCategoryRow
              category={product.category}
              key={product.category} />
          );
        }
        rows.push(
          <ProductRow
            product={product}
            key={product.name}
          />
        );
        lastCategory = product.category;
      });
  
      return (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Price</th>
            </tr>
          </thead>
          <tbody>{rows}</tbody>
        </table>
      );
    }
  }
  


class Counter extends Component {
    state = {
        count: 0 ,
        items:[
            {category: "dasd Goods", price: "$49.99", stocked: false, name: "Football"},
            {category: "adasd Goods", price: "$9.99", stocked: false, name: "Baseball"},
            {category: "asd Goods", price: "$29.99", stocked: false, name: "Basketball"},
            {category: "asd", price: "$99.99", stocked: false, name: "iPod Touch"},
            {category: "asd", price: "$399.99", stocked: false, name: "iPhone 5"},
            {category: "asda", price: "$199.99", stocked: false, name: "Nexus 7"}
          ]
    };


    render() { 

        return (
            <div>
            <div>{this.header()}</div>
            <CardDeck className="m-2 group">
            <ul className="list-group list-group-horizontal align-items-stretch flex-wrap">
                {this.renderTags()}
            </ul>
            </CardDeck>
            </div>
        );
    }

    header(){

        return (
                <div className="header">
            <span className={this.getBadgeClasses()}>{this.formatCounter()}</span>
            <button onClick={this.handelerIncrement} className="btn btn-secondary btn-sm">Increacment</button>
                    </div>
        );
    }

    handelerIncrement = () =>{
    this.setState({ count: this.state.count + 1 });
    }
    renderTags(){
        return this.state.items.category.map( tag => 
            <li className="list-group-item border-0">
         <Card key={tag} className="col-sm m-2" style={{ width: '18rem' }}>
            <Card.Img variant="top" src="https://developer.softbankrobotics.com/sites/default/files/2019-10/pepper_selector_2NEW.png" />
            <Card.Body>
                <Card.Title>{tag}</Card.Title>
                <Card.Text>
                {tag}
                </Card.Text>
                <Button onClick={this.handelerIncrement} variant="primary">Add To Card</Button>
            </Card.Body>
            </Card> 
            </li>
        )
    }
    getBadgeClasses() {
        let classes = "badge m-3 badge-";
        classes += this.state.count == 0 ? "warning" : "primary";
        return classes;
    }

    formatCounter(){
        const {count} = this.state.count;
        return count == 0 ? "Zero" : this.state.count;
    }
}

 
export default Counter;
